  jobStatus,
  COUNT(*) as job_count,
  COUNT(completed_date) as has_completed,
  COUNT(appointment_date) as has_appointment,
  COUNT(invoice_date) as has_invoice,
  MIN(appointment_date) as min_appt_date,
  MAX(appointment_date) as max_appt_date,
  MIN(invoice_date) as min_inv_date,
  MAX(invoice_date) as max_inv_date,
  MIN(completed_date) as min_comp_date,
  MAX(completed_date) as max_comp_date
FROM job_analysis
GROUP BY 1
ORDER BY job_count DESC